<?phpfunction getPostViews(){	$postID 		= get_the_ID();    $count_key 		= THEME_NAME.'_post_views';    $count 			= get_post_meta($postID, $count_key, true);	    if($count==''){        update_post_meta($postID, $count_key, '1');        return '1 '.__('Views', THEME_NAME);    }    return $count.' '.__('Views', THEME_NAME);}  add_action('wp_head', 'setPostViews');function setPostViews() {	if(is_singular()) {			$postID 	= get_the_ID();		$count_key 	= THEME_NAME.'_post_views';		$count 		= get_post_meta($postID, $count_key, true);				if($count==''){			$count = 1;			update_post_meta($postID, $count_key, '1');		}else{			$count++;			update_post_meta($postID, $count_key, $count);		}			}}?>